loadStory = "\n query SELECT_ENTRYSTORY(\n $pageParam: PageParam\n $query: String\n $user: String\n $category: String\n $term: String\n $prefix: String\n $progress: String\n $discussType: String\n $searchType: String\n $searchAfter: JSON\n){\n discussList(\n pageParam: $pageParam\n query: $query\n user: $user\n category: $category\n term: $term\n prefix: $prefix\n progress: $progress\n discussType: $discussType\n searchType: $searchType\n searchAfter: $searchAfter\n) {\n total\n list {\n \n\tid\n content\n created\n commentsLength\n likesLength\n user {\n \n id\n nickname\n username\n profileImage {\n \n id\n name\n label {\n \n ko\n en\n ja\n vn\n\n }\n filename\n imageType\n dimension {\n \n width\n height\n\n }\n trimmed {\n filename\n width\n height\n }\n\n }\n status {\n following\n follower\n }\n description\n role\n\n }\n image {\n \n id\n name\n label {\n \n ko\n en\n ja\n vn\n\n }\n filename\n imageType\n dimension {\n \n width\n height\n\n }\n trimmed {\n filename\n width\n height\n }\n\n }\n sticker {\n \n id\n name\n label {\n \n ko\n en\n ja\n vn\n\n }\n filename\n imageType\n dimension {\n \n width\n height\n\n }\n trimmed {\n filename\n width\n height\n }\n\n }\n isLike\n\n }\n searchAfter\n }\n }\n"

login = "\n    mutation (\n        $username: String!, \n        $password: String!, \n        $rememberme: Boolean, \n        $captchaValue: String, \n        $captchaKey: String,\n        $captchaType: String\n    ) {\n        signinByUsername (\n            username: $username, \n            password: $password, \n            rememberme: $rememberme, \n            captchaValue: $captchaValue, \n            captchaKey: $captchaKey,\n            captchaType: $captchaType\n        ) {\n            \n    id\n    username\n    nickname\n    role\n    isEmailAuth\n    isSnsAuth\n    isPhoneAuth\n    studentTerm\n    alarmDisabled\n    status {\n        userStatus\n    }\n    profileImage {\n        \n    id\n    name\n    label {\n        \n    ko\n    en\n    ja\n    vn\n\n    }\n    filename\n    imageType\n    dimension {\n        \n    width\n    height\n\n    }\n    trimmed {\n        filename\n        width\n        height\n    }\n\n    }\n    banned {\n        username\n        nickname\n        reason\n        bannedCount\n        bannedType\n        projectId\n        startDate\n        userReflect {\n            status\n            endDate\n        }\n    }\n    isProfileBlocked\n\n        }\n    }\n"

follow = "\n    mutation FOLLOW(\n        \n    $user: String\n\n    ) {\n        follow(\n            \n    user: $user\n\n        ) {\n            \n    id\n    \n    id\n    user {\n        id\n        username\n        nickname\n        profileImage {\n            \n    id\n    name\n    label {\n        \n    ko\n    en\n    ja\n    vn\n\n    }\n    filename\n    imageType\n    dimension {\n        \n    width\n    height\n\n    }\n    trimmed {\n        filename\n        width\n        height\n    }\n\n        }\n        status {\n            following\n            follower\n        }\n        isFollow\n        projects {\n            id\n            thumb\n            name\n        }\n    }\n\n    \n    id\n    follow {\n        id\n        username\n        nickname\n        profileImage {\n            \n    id\n    name\n    label {\n        \n    ko\n    en\n    ja\n    vn\n\n    }\n    filename\n    imageType\n    dimension {\n        \n    width\n    height\n\n    }\n    trimmed {\n        filename\n        width\n        height\n    }\n\n        }\n        status {\n            following\n            follower\n        }\n        isFollow\n        projects {\n            id\n            thumb\n            name\n        }\n    }\n\n\n        }\n    }\n"